#\n# @lc app=leetcode id=1 lang=python3\n#\n# [1] Two Sum\n#\n\n# @lc code=start\nclass Solution:\n    def twoSum(self, nums: List[int], target: int) -> List[int]:\n        hashmap = {}\n        for i, num in enumerate(nums):\n            complement = target - num\n            if complement in hashmap:\n                return [hashmap[complement], i]\n            hashmap[num] = i\n        return []\n# @lc code=end